CR_Animation.controller("cssAniCtrl",function(n){var e=document.getElementsByClassName("animate"),t=document.getElementById("cssPlay"),o=document.getElementById("cssPause");t.addEventListener("click",function(){for(i=0;i<e.length;i++)e[i].classList.remove("no-animation");console.log("Play CSS Animation")},!1),o.addEventListener("click",function(){for(i=0;i<e.length;i++)e[i].className+=" no-animation";console.log("Pause CSS Animation")},!1)});